mapper.select:
SELECT b.id id, b.title title, b.inserted inserted, COUNT(r.id) numOfReply,
b.views views
FROM Board b LEFT JOIN Reply r ON b.id = r.board_Id WHERE b.title LIKE #{search} GROUP BY b.id ORDER BY id DESC LIMIT #{from }, #{rowPerPage }

SELECT Count(id)
FROM Board
WHERE title LIKE #{search}

SELECT b.id id, b.title title, b.body body, b.inserted inserted, COUNT(r.id) numOfReply,
b.views views
FROM Board b LEFT JOIN Reply r ON b.id = r.board_Id WHERE b.id = #{id}

mapper.update:
UPDATE Board
Set title = #{title}, 
	body  = #{body},
	views = #{views} 
WHERE id = #{id}
